(function blockTextoMostra() {

    qtdeTagsTextoPT = texto.pt.length;
    qtdeTagsTextoEN = texto.pt.length;

    var divTextoPT = document.createElement('div');
    divTextoPT.setAttribute('class', 'sobre-expo pt');

    for (i = 0; i < qtdeTagsTextoPT; i++) {

        tagTextoPT = document.createElement(texto.pt[i][0]);
        tagTextoPT.innerHTML = texto.pt[i][1];
        tagTextoPT.setAttribute('class', texto.pt[i][2]);

        divTextoPT.appendChild(tagTextoPT);
    }
    document.getElementsByClassName("texto-mostra")[0].appendChild(divTextoPT);

    var divTextoEN = document.createElement('div');
    divTextoEN.setAttribute('class', 'sobre-expo en');

    for (i = 0; i < qtdeTagsTextoEN; i++) {

        tagTextoEN = document.createElement(texto.en[i][0]);
        tagTextoEN.innerHTML = texto.en[i][1];
        tagTextoEN.setAttribute('class', texto.en[i][2]);

        divTextoEN.appendChild(tagTextoEN);
    }
    document.getElementsByClassName("texto-mostra")[0].appendChild(divTextoEN);

}) ();

(function constructorSelecaoDivArtistas() {
    for (let i = 1; i < 52; i++) {

        // Creating Each option of artist
        divOptionArtist = document.createElement("div");
        divOptionArtist.setAttribute("class", "divOptionArtist");
        divOptionArtist.setAttribute("onclick", 'changeToArtist("artista_' + i + '")');

        // Each Artist Image
        artistImageOption = document.createElement("img");
        srcImg = 'images/avatars/lowres/artista_' + i + '.jpg';
        artistImageOption.setAttribute("src", srcImg);


        // Each Artist Name
        artistNameOption = document.createElement("p");
        artistNameOption.innerHTML = artistas[i];

        // Appending
        divOptionArtist.appendChild(artistImageOption);
        divOptionArtist.appendChild(artistNameOption);
        document.getElementById("selecaoDiv").appendChild(divOptionArtist);
    }
}) ();

